<div class='form_content'>    <h3><?=$page_title?></h3>    <form id="seo" action='<?=site_url($action)?>' method='post' enctype="multipart/form-data">        <table>            <tr>                <th><?=lang('label_controller_name')?> <span class='req_mark'>*</span></th>                <td>                  <select name="controller_name" class='text ui-widget-content ui-corner-all required width_200 dis_block' >                        <?=common::get_controller_name(set_value('controller_name',$controller_name))?>                  </select>                  <?=form_error('controller_name','<span>','</span>')?>                                    </td>            </tr>            <tr>                <th><?=lang('label_title')?> <span class='req_mark'>*</span></th>                <td><input type='text' name='title' value='<?=set_value('title',$title)?>' class='text ui-widget-content ui-corner-all required width_200' />                  <?=form_error('name','<span>','</span>')?>                </td>            </tr>           <tr>                <th><?=lang('label_meta_tag')?> <span class='req_mark'>*</span></th>                <td>                    <textarea name='meta_tag' class='ui-widget-content ui-corner-all' style="width:300px; height:100px;"><?=set_value('meta_tag',$meta_tag)?></textarea>                    <?=form_error('succ_desc','<span>','</span>')?>                </td>            </tr>             <tr>                <th><?=lang('label_meta_keyword')?> <span class='req_mark'>*</span></th>                <td>                    <textarea name='meta_keyword' class='ui-widget-content ui-corner-all' style="width:300px; height:100px;"><?=set_value('meta_keyword',$meta_keyword)?></textarea>                    <?=form_error('succ_desc','<span>','</span>')?>                </td>            </tr>            <tr>                <th><?=lang('label_meta_desc')?> <span class='req_mark'>*</span></th>                <td>                    <textarea name='meta_description' class='ui-widget-content ui-corner-all' style="width:300px; height:100px;"><?=set_value('meta_description',$meta_description)?></textarea>                    <?=form_error('succ_desc','<span>','</span>')?>                </td>            </tr>            <tr><th>&nbsp;</th><td><input type='submit' name='save' value="<?=lang('label_save')?>" class='button' /> <input type='button' name='cancel' value="<?=lang('label_cancel')?>" class='cancel' onclick="window.history.back(-1)" /></td></tr>        </table>    </form></div>